mapper.select:
select 
from IUS_MENU where parent_id =#{parentId}
  and show_flag = 1
  and site =#{site}
ORDER BY seq

select

from ius_menu
where 1=1

  and parent_id = #{parentId,jdbcType=DECIMAL}


  and leaf = #{leaf,jdbcType=DECIMAL}


  and site = #{site,jdbcType=DECIMAL}

order by seq

select

from ius_menu
where id = #{id,jdbcType=DECIMAL}

select 

from ius_menu
where 1=1

  and site = #{site,jdbcType=DECIMAL}


  and channel = #{channel,jdbcType=DECIMAL}
