mapper.select:
select
  COUNT(1)
from ius_password
where user_id = #{userId,jdbcType=VARCHAR}
and type = #{type,jdbcType=DECIMAL}

select 

from ius_password
where user_id = #{userId,jdbcType=VARCHAR}
  and type = #{type,jdbcType=DECIMAL}

mapper.delete:
delete from ius_password
where user_id = #{userId,jdbcType=VARCHAR}
  and type = #{type,jdbcType=DECIMAL}

mapper.update:
update ius_password
set password = #{password,jdbcType=VARCHAR},
  update_time =systimestamp
where user_id = #{userId,jdbcType=VARCHAR}
  and type = #{type,jdbcType=DECIMAL}
